div.script:
if (window.XMLHttpRequest) //firefox et autres
{
xmlhttpSelection = new XMLHttpRequest();
xmlhttpSelection.onreadystatechange = affiche_reponse;
xmlhttpSelection.open("GET", "../cgi-bin/bin2/410/perl_nbre_articles.pl?randomVar=" + Math.random(),true);
xmlhttpSelection.send(null);
}
else
{
if (window.ActiveXObject) //internet explorer
{
isIE = true;
xmlhttpSelection = new ActiveXObject("Microsoft.XMLHTTP");
if (xmlhttpSelection)
{
xmlhttpSelection.onreadystatechange = affiche_reponse;
xmlhttpSelection.open("GET", "../cgi-bin/bin2/410/perl_nbre_articles.pl?randomVar=" + Math.random(),true);
xmlhttpSelection.send(null);
}
}
}
function affiche_reponse() {
if (xmlhttpSelection.readyState == 4)
{
if (xmlhttpSelection.status == 200)
{
var reponse=xmlhttpSelection.responseText;
var reponseFormat=reponse.replace(/(^\s*)|(\s*$)/g,'');
if ((reponseFormat!=null) && (reponseFormat!=""))
{
document.getElementById("qte_mini_panier").innerHTML=reponseFormat;
}
}
else
{
alert("Il y a eu un problème lors de la connexion:\n"+xmlhttpSelection.statusText);
}
}
}

