function focusMotValue(input)
{
if (document.getElementById && document.getElementById(input).value == 'Recherchez un article')
document.getElementById(input).value='';
}
function blurMotValue(input)
{
if (document.getElementById && document.getElementById(input).value == '')
document.getElementById(input).value='Recherchez un article';
}

function categorySelectJS(categoryMenu)
{
if(categoryMenu.options[categoryMenu.selectedIndex].value=="")
{
alert('Veuillez selectionner une gamme d\\\'articles dans le menu.');
}
else
{
window.location.href = "liste_produits.cfm?type=" + categoryMenu.options[categoryMenu.selectedIndex].value + "&code_lg=lg_fr";
categoryMenu.selectedIndex=0;
}
}

