function affiche_nav_verti(id) {
var bloc = document.getElementById('nav_verti_smenutri'+id);
var parent = document.getElementById('nav_verti_image_treeview'+id);
if (bloc && parent){
if (bloc.style.display=='none'){
bloc.style.display='block';
parent.className='nav_verti_image_treeview_open';
}
else{
bloc.style.display='none';
parent.className='nav_verti_image_treeview_close';
}
}
}

