function rech_multi_clean()
{
if (document.form_rech_multi.mot.value == 'Rechercher :')
document.form_rech_multi.mot.value = '';
}
function isNumeric(Data)
{
numChars = ".0123456789";
var isNum = true;
var index = 0;
while ((index < Data.length) && (isNum))
{
isNum = (numChars.indexOf(Data.charAt(index)) != -1);
index ++;
}
return isNum;
}
function rech_multi_submit(tri)
{
rech_multi_clean();
if (tri == 0)
document.form_rech_multi.triage.value = 0;
if ( (isNumeric(document.form_rech_multi.prix_min.value))
&& (isNumeric(document.form_rech_multi.prix_max.value)) )
{
if (Number(document.form_rech_multi.prix_min.value) <= Number(document.form_rech_multi.prix_max.value))
{
document.form_rech_multi.submit();
}
else
{
if (document.form_rech_multi.prix_max.value == '')
document.form_rech_multi.submit();
else
window.alert('Attention, le prix minimum doit être inférieur au prix maximum.');
}
}
else {
window.alert('Veuillez entrer un nombre.');
if (!isNumeric(document.form_rech_multi.prix_min.value))
{
document.form_rech_multi.prix_min.value = '';
}
if (!isNumeric(document.form_rech_multi.prix_max.value))
{
document.form_rech_multi.prix_max.value = '';
}
}
}
