function tri(classement,ordre)
{
document.form_rech_multi.triage.value = 1;
if (classement == 1)
document.form_rech_multi.classement.value = "produit_marque.nom_marque";
if (classement == 2)
{
if ('#code_lg#' == "lg_us")
document.form_rech_multi.classement.value = "produit.nom_us";
else
document.form_rech_multi.classement.value = "produit.nom_produit";
}
if (classement == 3)
document.form_rech_multi.classement.value = "prix_unitaire";
if (ordre == 1)
document.form_rech_multi.ordre.value = "ASC";
if (ordre == 2)
document.form_rech_multi.ordre.value = "DESC";
rech_multi_submit(1);
}
